import React, {useState} from 'react';
import c from './counter.module.css'
import {Counter} from "./components/Counter";
import {SetCounter} from "./components/SetCounter";

function App() {

    let [minValue, setMinValue] = useState(0)
    let [maxValue, setMaxValue] = useState(5)
    let [value, setValue] = useState(minValue)

    let [error, setError] = useState<string | null>(null)
    let [settingsError, setSettingError] = useState<string | null>(null)


    if (minValue < 0 || maxValue < minValue || minValue > maxValue) {
        setSettingError('Введите корректное значение')
    }


    const counterIncrement = () => {
        setValue(++value)
        if (value >= maxValue) {
            setError('Предельное значение')
        }
    }

    const counterReset = () => {
        setValue(minValue)
        setError(null)
    }

    const setValueMin = (value: number) => {
        setMinValue(value)
    }

    const setValueMax = (value: number) => {
        setMaxValue(value)
    }

    const counterHandler = () => {
        setValue(minValue)
        setError(null)
    }
    return (

        <>
            <div className={c.container}>
                <Counter value={value}
                         increment={counterIncrement}
                         reset={counterReset}
                         error={error}/>
                <SetCounter minValue={minValue}
                            maxValue={maxValue}
                            minValueSet={setValueMin}
                            maxValueSet={setValueMax}
                            counterHandler={counterHandler}
                            error={error}
                />
                {settingsError && <div>{settingsError}</div>}

            </div>

        </>
    );
}

export default App;
