import React, {useState} from 'react';
import {CounterInput} from "./CounterInput";
import {CounterButton} from "./CounterButton";
import c from '../counter.module.css'

export type SetCounterPropsType = {
    minValue: number
    maxValue: number
    minValueSet: (value:number) => void
    maxValueSet: (value:number) => void
    counterHandler: () => void
    error:string | null
}


export const SetCounter = (props: SetCounterPropsType) => {

    let [settingsError, setSettingError] = useState<string | null>(null)


    const disableButton = () => {
        if (props.minValue < 0 || props.maxValue < props.minValue || props.minValue > props.maxValue) {
            setSettingError('Введите корректное значение')
        }
    }


    return (
     <div className={c.setCounter}>
        <div className={c.setCounter__wrapper}>
            <div className={c.setCounter__minMaxInputs}>
                <CounterInput value={props.minValue} callback={props.minValueSet} className={c.setCounter__input}/>
                <CounterInput value={props.maxValue} callback={props.maxValueSet} className={c.setCounter__input}/>
            </div>
            <div className={c.setCounter__buttonSet}>
                <CounterButton name={'Set'} callback={props.counterHandler}/>
                {settingsError && <div>{settingsError}</div>}
            </div>
        </div>
     </div>
   );
};
