import React, {useState} from 'react';
import {CounterInput} from "./CounterInput";
import {CounterButton} from "./CounterButton";

export type SetCounterPropsType = {
    minValue: number
    maxValue: number
    minValueSet: (value:number) => void
    maxValueSet: (value:number) => void
    counterHandler: () => void
    error:string
}


export const SetCounter = (props: SetCounterPropsType) => {

    if (props.minValue < 0) {
        props.error
    }

    return (
     <div>
        <div>
            <div>
                <CounterInput value={props.minValue} callback={props.minValueSet}/>
                <CounterInput value={props.maxValue} callback={props.maxValueSet}/>
            </div>
            <div>
                <CounterButton name={'Set'} callback={props.counterHandler}/>
            </div>
        </div>
     </div>
   );
};
