import React, {useState} from 'react';
import c from './counter.module.css'
import {Counter} from "./components/Counter";
import {SetCounter} from "./components/SetCounter";

function App() {

    const getValueFromSet = () => {
    }

    let [minValue, setMinValue] = useState(2)
    let [maxValue, setMaxValue] = useState(6)
    let [value, setValue] = useState(minValue)

    let [error, setError] = useState<string | null>(null)


    const counterIncrement = () => {
        if (value >= maxValue || value < 0) {
            setError('Предельное значение')
        }
        setValue(value + 1)
    }

    const counterReset = () => {
        setValue(minValue)
        setError(null)
    }

    const setValueMin = (value: number) => {
        setMinValue(value)
    }

    const setValueMax = (value: number) => {
        setMaxValue(value)
    }

    return (
        <>
            <div className={c.container}>
                <Counter value={value}
                         increment={counterIncrement}
                         reset={counterReset}
                         error={error}/>
                <SetCounter value={value}
                            minValue={setValueMin}
                            maxValue={setValueMax}/>
            </div>

        </>
    );
}

export default App;
